import React, { useState, useCallback, useRef } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image,
  Dimensions,
  ImageBackground,
} from "react-native";
import { phoneNumberValidation } from "../../api/api";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useFocusEffect, useRoute } from "@react-navigation/native";
import { PixelRatio } from "react-native";
import { RFPercentage, RFValue } from "react-native-responsive-fontsize";

const { width, height } = Dimensions.get("window");
const logoSize = Math.min(width, height) * 0.4;

const windowWidth = Dimensions.get("window").width;
const windowHeight = Dimensions.get("window").height;

const Login = (props) => {
  const { navigation } = props;
  const route = useRoute();
  const { getstatus } = route.params;
  // State variables
  const [phoneNumber, setPhoneNumber] = useState("");
  const [otp, setOTP] = useState(["", "", "", ""]);
  const [showLogin, setShowLogin] = useState(true);

  const getResponsiveImageSource = () => {
    const pixelRatio = PixelRatio.get();
    if (pixelRatio <= 1) {
      return require("../../../assets/Login/logo1x.png");
    } else if (pixelRatio <= 2) {
      return require("../../../assets/Login/logo2x.png");
    } else {
      return require("../../../assets/Login/logo3x.png");
    }
  };

  const imageSource = getResponsiveImageSource();

  // Function to clear data when the component is first loaded
  const clearDataOnFirstLoad = useCallback(async () => {
    try {
      await AsyncStorage.removeItem("UserData");
    } catch (error) {
      console.error("Error clearing AsyncStorage:", error);
      alert("An error occurred while clearing data.");
    }
  }, []);

  useFocusEffect(clearDataOnFirstLoad);
  const clearAndReset = useCallback(async () => {
    try {
      await AsyncStorage.removeItem("UserData");
      setPhoneNumber("");
      setOTP(["", "", "", ""]);
      setShowLogin(true);
    } catch (error) {
      console.error("Error clearing AsyncStorage:", error);
      alert("An error occurred while clearing data.");
    }
  }, []);

  useFocusEffect(clearAndReset);
  const handleNextOrVerify = async () => {
    if (showLogin) {
      // Check if phone number is valid (for simplicity, checking if it's 10 digits)
      if (phoneNumber.length === 10 || !phoneNumber) {
        try {
          if (!phoneNumber) {
            console.log("{}{}{}{}{}{}{}{}{}");
            getstatus(false);
            // Skip phone number validation and navigate to Home
            await AsyncStorage.removeItem("UserData");
            navigation.navigate("Slider", { isLoggedIn: false });
            return;
          } else {
          }
          // Call the phoneNumberValidation function to validate the number
          const validationResponse = await phoneNumberValidation({
            number: phoneNumber,
          }).then((res) => {
            if (res.status === 201) {
              alert("Invalid Phone Number. Please enter a valid phone number.");
            } else if (res.status === 200) {
              // Store data in local storage
              console.log(res.data[0].Name);
              getstatus(true, res.data[0].Name);
              const userData = res.data; // Assuming res.data contains user data
              AsyncStorage.setItem("UserData", JSON.stringify(userData))
                .then(() => {
                  console.log("Data stored in local storage:", userData);
                })
                .catch((error) => {
                  console.error("Error storing data in local storage:", error);
                });

              setShowLogin(false); // Switch to OTP view
            } else {
              console.log("No");
            }
          });
        } catch (error) {
          console.error("Error validating phone number:", error);
          alert("An error occurred while validating the phone number.");
        }
      } else {
        alert("Invalid Phone Number. Please enter a 10-digit phone number.");
      }
    } else {
      // Implement OTP verification logic here.
      // For simplicity, we'll just check if the OTP is "1234".
      const enteredOTP = otp.join(""); // Concatenate OTP digits
      if (enteredOTP === "1234") {
        // Navigate to the Home screen or your desired destination.
        navigation.navigate("Slider");
      } else {
        // Handle invalid OTP (display an error message, etc.).
        alert("Invalid OTP. Please try again.");
      }
    }
  };
  const otpInput = [useRef(), useRef(), useRef(), useRef()];
  // Function to handle OTP digit input
  const handleOTPDigitChange = (index, text) => {
    const newOTP = [...otp];
    newOTP[index] = text;
    setOTP(newOTP);

    if (text && index < 3) {
      otpInput[index + 1].current.focus();
    }
    if (!text && index > 0) {
      otpInput[index - 1].current.focus();
    }
  };

  const buttonLabel = showLogin ? (phoneNumber ? "Next" : "Skip") : "Verify";
  return (
    <View style={styles.container1}>
      <View style={styles.imagebox}>
        <ImageBackground
          source={require("../../../assets/Login/LoginBackground.png")}
          style={styles.backgroundImage1}
          resizeMode="stretch"
        >
          <View style={styles.loginLogoContainer}>
            <Image
              source={imageSource}
              style={[styles.loginLogo, { height: logoSize, width: logoSize }]}
            />
          </View>
        </ImageBackground>
        <View style={styles.contentContainer}>
          <Text style={styles.title}>Welcome!</Text>
          <Text style={styles.subtitle}>
            {showLogin
              ? "Please Login To Continue"
              : "Please Login To Continue"}
          </Text>
          {showLogin ? (
            <View style={styles.inputContainer}>
              <View style={styles.phoneIconContainer}>
                <Image
                  source={require("../../../assets/Login/phone.png")}
                  style={styles.phoneIcon}
                />
              </View>
              <TextInput
                style={[styles.input, { color: "black" }]}
                placeholder="Phone Number"
                placeholderTextColor="#0000004D"
                keyboardType="numeric"
                maxLength={10}
                value={phoneNumber}
                onChangeText={(text) => {
                  const numericText = text.replace(/[^0-9]/g, "");
                  setPhoneNumber(numericText);
                }}
              />
            </View>
          ) : (
            <View style={styles.otpContainer}>
              {otp.map((digit, index) => (
                <TextInput
                  key={index}
                  style={styles.otpInput}
                  placeholder=""
                  keyboardType="numeric"
                  maxLength={1}
                  value={digit}
                  onChangeText={(text) => handleOTPDigitChange(index, text)}
                  ref={otpInput[index]}
                />
              ))}
            </View>
          )}
          <View style={{ width: "100%", height: 100 }}>
            <TouchableOpacity
              style={styles.button}
              onPress={handleNextOrVerify}
            >
              <Text style={styles.buttonText}>{buttonLabel}</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  contentContainer: {
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
    position: "absolute",
    bottom: 0,
  },
  title: {
    color: "white",
    fontSize: windowWidth * 0.07,
    // fontSize:RFPercentage(5),
    fontWeight: 700,
    marginBottom: "2%",
  },
  subtitle: {
    color: "rgba(255, 255, 255, 0.70)",
    fontSize: windowWidth * 0.04,
    // fontSize:RFPercentage(5),
    fontWeight: 700,
    marginBottom: "10%",
  },
  input: {
    flex: 1,
    fontSize: width >= 768 ? 35 : 20,
    height: height >= 1080 ? 80 : 50,
    paddingLeft: 5,
    backgroundColor: "white",
    borderTopRightRadius: 7,
    borderBottomRightRadius: 7,

    color: " rgba(0, 0, 0, 0.30)",
  },
  otpContainer: {
    flexDirection: "row",
    width: "50%",
    marginBottom: "10%",
    justifyContent: "space-between",
  },
  otpInput: {
    width: windowWidth * 0.1,
    justifyContent: "space-between",
    height: windowHeight * 0.07,
    borderColor: "gray",
    borderWidth: 1,
    backgroundColor: "white",
    fontSize: width >= 768 ? 40 : 23,
    borderRadius: 7,
    textAlign: "center",
  },
  button: {
    backgroundColor: "#212121",
    width: width >= 768 ? 220 : 148,
    height: width >= 768 ? 80 : 50,
    borderRadius: 10,
    position: "absolute",
    justifyContent: "center",
    alignItems: "center",
    bottom: 0,
    right: 0,
  },
  buttonText: {
    color: "white",
    fontSize: width >= 768 ? 40 : 43,
    fontWeight: 700,
    textAlign: "center",
  },
  phoneIcon: {
    height: width >= 768 ? 35 : 20,
    width: width >= 768 ? 35 : 20,
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    width: "90%",
    height: 50,
    borderColor: "gray",
    borderRadius: 7,
    marginBottom: windowHeight * 0.046,
    justifyContent: "center",
  },
  phoneIconContainer: {
    height: height >= 1080 ? 80 : 50,
    width: width >= 768 ? 80 : 50,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#FFF",
    borderRadius: 7,
    borderRightWidth: 3,
    borderColor: "#212121",
  },
  container1: {
    flex: 1,
    padding: 20,
  },
  backgroundImage1: {
    flex: 1,
    resizeMode: "stretch",
    width: "100%",
  },
  loginContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  loginLogoContainer: {
    position: "absolute",
    top: "40%",
    left: "50%",
    transform: [{ translateX: -logoSize / 2 }, { translateY: -logoSize / 2 }],
  },
  loginLogo: {
    resizeMode: "contain",
  },
  imagebox: {
    flex: 1,
  },
});

export default Login;
