import React, { useState } from "react";
import { View, Text, TouchableOpacity, Image, StyleSheet } from "react-native";

export default function FilterComponent({
  categoriesData,
  selectedCategories, // Pass the selected categories as a prop
  setSelectedCategories, // Function to update selected categories
  clearFilters,
  applyFilters,
}) {
  const handleCategorySelect = (category) => {
    if (selectedCategories.includes(category)) {
      setSelectedCategories(selectedCategories.filter((item) => item !== category));
    } else {
      setSelectedCategories([...selectedCategories, category]);
    }
  };

  const handleCloseFilter = () => {
    clearFilters();
  };

  const handleReset = () => {
    setSelectedCategories([]);
  };

  const isApplyDisabled = selectedCategories.length === 0; // Check if no category is selected

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Categories</Text>
        <TouchableOpacity onPress={handleCloseFilter}>
          <Image
            source={require("../../../assets/FilterIcon/Close.png")}
            style={styles.closeIcon}
          />
        </TouchableOpacity>
      </View>

      <View style={styles.categoriesContainer}>
        {categoriesData.map((category, index) => (
          <TouchableOpacity
            key={category.id}
            onPress={() => handleCategorySelect(category.Category)}
            style={[
              styles.categoryItem,
              {
                borderColor: selectedCategories.includes(category.Category)
                  ? "black"
                  : "gray",
              },
            ]}
          >
            <View
              style={[
                styles.categoryIcon,
                {
                  marginRight: 10,
                },
              ]}
            >
              {selectedCategories.includes(category.Category) && (
                <View style={styles.selectedIcon} />
              )}
            </View>
            <Text style={styles.categoryText}>{category.Category}</Text>
          </TouchableOpacity>
        ))}
      </View>

      <View style={styles.buttonsContainer}>
        <TouchableOpacity
          onPress={handleReset}
          style={[styles.button, { marginRight: 40 }]}
        >
          <Text style={styles.buttonText}>Reset</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={applyFilters}
          style={[
            styles.button,
            { marginLeft: 40 },
            { backgroundColor: isApplyDisabled ? "gray" : "black" }, // Change button color based on disabled state
          ]}
          disabled={isApplyDisabled} // Set disabled attribute based on the condition
        >
          <Text style={styles.buttonText}>Apply</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  headerText: {
    fontSize: 25,
    fontWeight: "bold",
  },
  closeIcon: {
    width: 35,
    height: 35,
  },
  categoriesContainer: {
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-between",
  },
  categoryItem: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 5,
    width: "48%", // Two items in a row with some spacing
    borderWidth: 1,
    borderRadius: 5,
    padding: 5,
  },
  categoryIcon: {
    width: 20,
    height: 20,
    borderRadius: 10,
    borderWidth: 2,
    justifyContent: "center",
    alignItems: "center",
  },
  selectedIcon: {
    width: 12,
    height: 12,
    borderRadius: 6,
    backgroundColor: "black",
  },
  categoryText: {
    fontSize: 12, // Adjust font size as needed
    marginLeft: 5,
  },
  buttonsContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 10,
  },
  button: {
    backgroundColor: "black",
    padding: 10,
    alignItems: "center",
    borderRadius: 5,
    flex: 1,
  },
  buttonText: {
    color: "white",
  },
});
