import React, { useEffect, useLayoutEffect, useState } from "react";
import {
  Text,
  View,
  Image,
  Dimensions,
  FlatList,
  TouchableOpacity,
} from "react-native";
import {
  getProductName,
  getWishlistData,
  getAddWishlist,
  DeleteWishlist,
} from "../../api/api";
import styles from "./styles";
import { FontAwesome } from "@expo/vector-icons";
import ButtomNavigation from "../../components/AppFooter/ButtomNavigation";
import MenuBackArrow from "../../components/menubackarrow/menubackarrow";
import SearchBar from "../../components/SearchBar/searchbar";
import Filter from "../../components/Filter/Filter";

import { ActivityIndicator } from "react-native";
export default function AllArticle(props) {
  const { navigation } = props;
  const [finalData, setFinalData] = useState([]);
  const [nameDatas, setNameDatas] = useState([]);
  const [isFilterVisible, setIsFilterVisible] = useState(false);
  const [selectedprd, setSelectprd] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedPriceRange, setSelectedPriceRange] = useState([]);
  const [searchText, setSearchText] = useState(""); // To store the search text
  const [minArticleRate, setMinArticleRate] = useState(null);
  const [maxArticleRate, setMaxArticleRate] = useState(null);
  const [noArticlesFound, setNoArticlesFound] = useState(false);

  const { width, height } = Dimensions.get("window");

  // uploard url image
  const baseImageUrl = "https://colorhunt.in/colorHuntApi/public/uploads/";

  const openFilter = () => {
    setIsFilterVisible((prev) => !prev); // Toggle the Filter component visibility
  };

  const getCategoriesname = async () => {
    const res = await getProductName();
    if (res.status === 200) {
      // console.log(res.data);
      setNameDatas(res.data);
      setFinalData(res.data);
      setIsLoading(false);
    }
  };
  const rmvProductWishlist = async (i) => {
    let data = {
      party_id: 197,
      article_id: i.Id,
    };
    try {
      await DeleteWishlist(data).then((res) => {
        if (res.status === 200) {
          getWishlist();
        }
      });
    } catch (error) {
      console.log(error);
    }
  };

  // ------- add product in wishlist start-------------
  const getWishlist = async () => {
    const data = {
      party_id: 197,
    };
    const result = await getWishlistData(data).then((res) => {
      setSelectprd(res.data);
    });
  };

  const addArticleWishlist = async (i) => {
    let data = {
      user_id: 197,
      article_id: i.Id,
    };
    try {
      await getAddWishlist(data).then((res) => {
        getWishlist();
      });
    } catch (error) {
      console.log(error);
    }
  };
  const convertToTitleCase = (str) => {
    return str
      .toLowerCase()
      .split("-") // Split the string at hyphens
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
      .join("-"); // Join the words with hyphens
  };
  useEffect(() => {
    getCategoriesname();
    getWishlist();
  }, []);

  useLayoutEffect(() => {
    navigation.setOptions({
      headerLeft: () => (
        <MenuBackArrow
          onPress={() => {
            navigation.goBack();
          }}
        />
      ),
      headerTitle: () => <View />,
      headerRight: () => (
        <View
          style={{
            marginHorizontal: 10,
            width: "auto",
            height: "auto",
            padding: 4,
          }}
        >
          <TouchableOpacity
            onPress={() => {
              navigation.navigate("Profile");
            }}
          >
            <Image
              style={styles.searchIcon}
              source={require("../../../assets/Nevbar/Profile.png")}
            />
          </TouchableOpacity>
        </View>
      ),
    });
  }, []);

  useEffect(() => {
    filterData();
  }, [searchText, nameDatas]);

  const filterData = () => {
    if (searchText === "") {
      setFinalData(nameDatas);
    } else {
      const filtered = nameDatas.filter(
        (item) =>
          item.ArticleNumber.toString().includes(searchText.toString()) ||
          item.Category.toLowerCase().includes(searchText.toLowerCase()) ||
          item.ArticleRate.toString().includes(searchText.toString()) ||
          item.StyleDescription.toLowerCase().includes(
            searchText.toLowerCase()
          ) ||
          item.Subcategory.toLowerCase().includes(searchText.toLowerCase())
      );
      console.log(filtered.length, "length");
      setFinalData(filtered);
      console.log(finalData.length, "FD");
      setNoArticlesFound(filtered.length === 0);
    }
  };

  const renderItem = ({ item }) => (
    <View
      style={{
        alignItems: "center",
        height: "auto",
        width: width >= 768 ? "22%" : "44.8%",
        margin: 10,
        borderRadius: 10,
        borderColor: "gray",
        backgroundColor: "#FFF",
        shadowColor: "gray",
        shadowOpacity: 0.4,
        shadowRadius: 4,
        elevation: 10,
        shadowOffset: {
          width: 0,
          height: 0,
        },
      }}
    >
      <View id={item.id} style={styles.producticones}>
        {selectedprd.some((i) => i.Id === item.Id) ? (
          <TouchableOpacity
            onPress={() => {
              rmvProductWishlist(item);
            }}
          >
            <FontAwesome
              name="heart"
              style={[
                styles.icon,
                // isLoggedin === false ? styles.disabledIcon : null,
              ]}
            />
          </TouchableOpacity>
        ) : (
          <TouchableOpacity
            onPress={() => {
              addArticleWishlist(item);
            }}
          >
            <FontAwesome
              name="heart-o"
              style={[
                styles.disabledIcon,
                // isLoggedin === false ? styles.disabledIcon : null,
              ]}
            />
          </TouchableOpacity>
        )}
      </View>
      <View
        style={{
          width: "100%",
          justifyContent: "center",
          alignItems: "center",
          elevation: 20,
          borderColor: "gray",
          shadowColor: "#c0c0c0",
          borderRadius: 10,
        }}
      >
        <Image
          source={{ uri: baseImageUrl + item.Photos }}
          style={{
            width: "90%",
            height: 180,
            flex: 1,
            resizeMode: "contain",
            borderRadius: 10,
            zIndex: 1,
            marginTop: 10,
          }}
        />
      </View>
      <View
        style={{
          width: "100%",
          marginBottom: 10,
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <TouchableOpacity
          onPress={() =>
            navigation.navigate("DetailsOfArticals", { id: item.Id })
          }
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            marginTop: 0,
          }}
        >
          <View style={{ width: 178, alignItems: "center", paddingTop: 10 }}>
            <Text
              style={{ fontWeight: "bold", fontSize: width >= 768 ? 18 : 12 }}
            >
              {item.ArticleNumber}
            </Text>
            <Text style={{ fontSize: width >= 768 ? 15 : 10 }}>
              {convertToTitleCase(item.Category)}
            </Text>
            <Text
              style={{ fontWeight: "bold", fontSize: width >= 768 ? 18 : 12 }}
            >
              {"₹" + item.ArticleRate + ".00"}
            </Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
  const handleFilterChange = (categories, priceRange) => {
    setSelectedCategories(categories);
    setSelectedPriceRange(priceRange);
    setSearchText(""); // Reset the search text

    // Filter based on selected categories and price range
    const filteredData = nameDatas.filter(
      (item) =>
        selectedCategories.includes(item.Category) &&
        item.ArticleRate >= selectedPriceRange[0] &&
        item.ArticleRate <= selectedPriceRange[1]
    );
    setFinalData(filteredData);
    console.log("handle Filter chnage", finalData.length);
    setNoArticlesFound(filteredData.length === 0);
  };
  const handleCloseFilter = () => {
    setIsFilterVisible((prev) => !prev);
  };

  useEffect(
    () => {
      console.log(selectedCategories, "sca");
      console.log(selectedPriceRange, "spa");
      const abc = nameDatas.filter(
        (item) =>
          (!selectedCategories.length ||
            selectedCategories.includes(item.Category)) &&
          item.ArticleRate >= selectedPriceRange[0] &&
          item.ArticleRate <= selectedPriceRange[1]
      );
      setFinalData(abc);
      console.log("useeffect", finalData.length);
    },
    [selectedCategories],
    [selectedPriceRange]
  );
  useEffect(() => {
    const minRate = finalData.reduce((min, item) => {
      const articleRate = parseFloat(item.ArticleRate); // Convert the article rate to a number
      return articleRate < min ? articleRate : min;
    }, Infinity);

    const maxRate = finalData.reduce((max, item) => {
      const articleRate = parseFloat(item.ArticleRate); // Convert the article rate to a number
      return articleRate > max ? articleRate : max;
    }, -Infinity);

    setMinArticleRate(minRate);

    setMaxArticleRate(maxRate);
  }, [finalData]);
  return (
    <>
      {isLoading ? (
        <View style={styles.loader}>
          <ActivityIndicator size="large" color="black" />
        </View>
      ) : (
        <View
          style={{ width: "100%", height: "100%", backgroundColor: "#FFF" }}
        >
          <View
            style={{
              flexDirection: "row",
              backgroundColor: "#FFF",
              alignItems: "center",
              width: "89%",
              paddingStart: 3,
              paddingTop: 10,
            }}
          >
            <SearchBar
              searchPhrase={searchText}
              setSearchPhrase={setSearchText}
            />
            <TouchableOpacity onPress={openFilter}>
              <Image
                source={require("../../../assets/filetr_icone.png")}
                style={{
                  width: width >= 768 ? 65 : 40, // Adjust the width for tablets
                  height: width >= 768 ? 65 : 40,
                  marginLeft: 10,
                  borderRadius: 10,
                }}
              />
            </TouchableOpacity>
          </View>
          <View>
            <Text
              style={{
                fontSize: width >= 768 ? 25 : 15,
                fontWeight: 700,
                paddingLeft: 15,
                height: width >= 768 ? 30 : 20,
                alignItems: "center",
                marginTop: 10,
              }}
            >
              ALL Articles
            </Text>
          </View>
          <View
            style={{
              position: "relative",
              backgroundColor: "#FFF",
              width: "100%",
              height: "74%",
              top: 20,
              paddingHorizontal: 10,
            }}
          >
            {noArticlesFound ? (
              <Text
                style={{ textAlign: "center", fontSize: 16, marginTop: 20 }}
              >
                NO ARTICLES FOUND
              </Text>
            ) : (
              <FlatList
                style={{ backgroundColor: "#FFF" }}
                data={finalData}
                keyExtractor={(item) => item.Id}
                renderItem={renderItem}
                numColumns={width >= 768 ? 4 : 2}
                showsHorizontalScrollIndicator={false}
                contentContainerStyle={{ paddingVertical: 0 }}
              />
            )}
          </View>
          {/* </ScrollView> */}
          {isFilterVisible ? null : (
            <View
              style={{ position: "absolute", bottom: 0, left: 0, right: 0 }}
            >
              <ButtomNavigation navigation={navigation} page="home" />
            </View>
          )}

          {isFilterVisible && (
            <View
              style={{
                flex: 1,
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                width: "100%",
                height: "100%",
                position: "absolute",
                top: 0,
                left: 0,
                justifyContent: "flex-end",
              }}
            >
              <View
                style={{
                  width: "92%",
                  backgroundColor: "#FFF",
                  position: "absolute",
                  bottom: 0,
                  left: 1,
                  right: 0, // To make it span the full width
                  marginLeft: "4%", // Margin on the left side
                  padding: 10,
                  borderTopLeftRadius: 10, // Adjust the radius as needed
                  borderTopRightRadius: 10,
                }}
              >
                <Filter
                  status={false}
                  onFilterChange={handleFilterChange}
                  onCloseFilter={handleCloseFilter}
                  Scategories={selectedCategories}
                  minArticleRate={minArticleRate}
                  maxArticleRate={maxArticleRate}
                />
              </View>
            </View>
          )}
        </View>
      )}
    </>
  );
}
