import React, { useEffect, useLayoutEffect, useState } from "react";
import {
  Text,
  View,
  Image,
  ScrollView,
  TouchableOpacity,
  Modal,
  Dimensions,
} from "react-native";
// import Image from 'react-native-responsive-image';
import styles from "./styles";
import { FontAwesome } from "@expo/vector-icons";
import {
  getProductName,
  getcateGorywithphotos,
  getWishlistData,
  getAddWishlist,
  DeleteWishlist,
} from "../../api/api";
import ButtomNavigation from "../../components/AppFooter/ButtomNavigation";
import SearchBar from "../../components/SearchBar/searchbar";
import { ActivityIndicator } from "react-native";
import Filter from "../../components/Filter/Filter";
import CreateAccount from "../../components/CreateAccount/CreateAccount";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useFocusEffect } from "@react-navigation/native";
export default function HomeScreen(props) {
  const { navigation } = props;
  const [categoryName, setCategoryName] = useState();
  const [ApplyStatushBack, setApplyStatushBack] = useState(true);
  const [nameData, setNameData] = useState([]);
  const [nameDatas, setNameDatas] = useState([]);
  const [applyrData, setApplyData] = useState([]);
  const [selectedprd, setSelectprd] = useState([]);
  const [isFilterVisible, setIsFilterVisible] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedPriceRange, setSelectedPriceRange] = useState([]);
  const [finalData, setFinalData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchText, setSearchText] = useState("");
  const [filterDataSearch, setFilterDataSearch] = useState([]);
  const [minArticleRate, setMinArticleRate] = useState(null);
  const [maxArticleRate, setMaxArticleRate] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isCreateAccountVisible, setCreateAccountVisible] = useState(false);
  const { width, height } = Dimensions.get("window");
  // const textStyles = width >= 768 ? styles.tabletText : styles.phoneText;

  useEffect(() => {
    // Set isLoading to true initially
    setIsLoading(true);

    // Use setTimeout to change isLoading to false after a delay (e.g., 2000 milliseconds or 2 seconds)
    const timeoutId = setTimeout(() => {
      setIsLoading(false);
    }, 2000);

    // Clear the timeout when the component unmounts to prevent memory leaks
    return () => {
      clearTimeout(timeoutId);
    };
  }, []);
  const openFilter = () => {
    setIsFilterVisible((prev) => !prev); // Toggle the Filter component visibility
  };
  // ------- add product in wishlist start-------------
  const openCreateAccountModal = () => {
    console.log("done");
    setCreateAccountVisible(true);
  };
  const checkUserLoginforheader = async () => {
    try {
      const data = await AsyncStorage.getItem("UserData");
      if (data) {
        navigation.navigate("Profile");
      } else {
        openCreateAccountModal();
      }
    } catch (error) {
      console.error("Error while checking user data:", error);
      return false; // Handle errors by returning false or appropriate error handling
    }
  };

  const closeCreateAccountModal = () => {
    setCreateAccountVisible(false);
  };
  const getWishlist = async () => {
    const data = {
      party_id: 197,
    };
    const result = await getWishlistData(data).then((res) => {
      setSelectprd(res.data);
    });
  };
  function convertToTitleCase(str) {
    return str
      .toLowerCase()
      .split("-") // Split the string at hyphens or spaces
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
      .join("-"); // Join the words with spaces
  }
  const addArticleWishlist = async (i) => {
    let data = {
      user_id: 197,
      article_id: i.Id,
    };
    console.log("............111", data);
    try {
      await getAddWishlist(data).then((res) => {
        getWishlist();
      });
    } catch (error) {
      console.log(error);
    }
  };

  const rmvProductWishlist = async (i) => {
    console.log(i, "r");
    let data = {
      party_id: 197,
      article_id: i.Id,
    };
    console.log(data);

    try {
      await DeleteWishlist(data).then((res) => {
        if (res.status === 200) {
          getWishlist();
        }
      });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getCategoriesname();
    getWishlist();
  }, []);

  // uploard url image
  const baseImageUrl = "https://colorhunt.in/colorHuntApi/public/uploads/";
  //getCategoriesname
  // const getCategoriesname = async () => {
  //   try {
  //     const result1 = await getcateGorywithphotos();
  //     if (result1.status === 200) {
  //       setCategoryName(result1.data);
  //       setNameData(result1.data);
  //       setApplyData(result1.data);
  //     }

  //     const result2 = await getProductName();
  //     if (result2.status === 200) {
  //       setCategoryName(result2.data);
  //       setNameDatas(result2.data);
  //       setApplyData(result2.data);
  //       setFilterDataSearch(result2.data);
  //     }

  //     setIsLoading(false);
  //   } catch (error) {
  //     // Handle any errors that might occur during the API requests.
  //     console.error(error);
  //     setIsLoading(false); // Make sure to set isLoading to false in case of an error.
  //   }
  // };

  const getCategoriesname = async () => {
    try {
      // Make an API request to get category data with photos
      const result1 = await getcateGorywithphotos();

      // Check if the response status is 200 (success)
      if (result1.status === 200) {
        // Update state variables with the data from the API response
        setCategoryName(result1.data);
        setNameData(result1.data);
        setApplyData(result1.data);
      }

      // Make another API request to get product names
      const result2 = await getProductName();

      // Check if the response status is 200 (success)
      if (result2.status === 200) {
        // Update state variables with the data from the API response
        setCategoryName(result2.data);
        setNameDatas(result2.data);
        setApplyData(result2.data);
        setFilterDataSearch(result2.data);
      }

      // Set isLoading to false to indicate that data loading is complete
      setIsLoading(false);
    } catch (error) {
      // Handle any errors that might occur during the API requests.
      console.error(error);

      // Set isLoading to false in case of an error.
      setIsLoading(false);
    }
  };

  useEffect(() => {
    getCategoriesname();
  }, []);

  const viewAllArticles = () => {
    console.log(finalData.length, "lenght of navigation data ");
    navigation.navigate("AllArticle", { finalData });
  };

  useLayoutEffect(() => {
    navigation.setOptions({
      headerLeft: () => (
        <View
          style={{
            marginLeft: 5,
            width: 50,
            height: 100,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <TouchableOpacity
            onPress={() => {
              navigation.openDrawer();
            }}
          >
            <Image
              source={require("../../../assets/sidbarOpenIcone.png")}
              style={{
                width: width >= 768 ? 50 : 30,
                height: width >= 768 ? 50 : 30,
                // resizeMode: "contain",
                borderRadius: 5,
              }}
            ></Image>
          </TouchableOpacity>
        </View>
      ),
      headerTitle: () => <View />,
      headerRight: () => (
        <View
          style={{
            marginHorizontal: 10,
            width: "auto",
            height: "auto",
            padding: 4,
          }}
        >
          <TouchableOpacity
            onPress={() => {
              checkUserLoginforheader();
              // console.log(val,";;;;;;");
              // val===true?
              // :openCreateAccountModal()
            }}
          >
            <Image
              style={{
                resizeMode: "contain",
                width: width >= 768 ? 50 : 30,
                height: width >= 768 ? 50 : 30,
              }}
              source={require("../../../assets/Nevbar/Profile.png")}
            />
          </TouchableOpacity>
        </View>
      ),
      headerTitle: () => null, // Remove the header title
      headerStyle: {
        height: width >= 768 ? 120 : 70,
        // backgroundColor: "black",
      },
    });
  }, []);

  const handlePress = (item) => {
    navigation.navigate("CategorisWiseArticle", { item1: item });
  };

  const filterData = () => {
    if (searchText === "") {
    } else {
      console.log(searchText);
      const filtered = filterDataSearch.filter(
        (item) =>
          item.ArticleNumber.toString().includes(searchText.toString()) ||
          item.Category.toLowerCase().includes(searchText.toLowerCase()) ||
          item.ArticleRate.toString().includes(searchText.toString()) ||
          item.StyleDescription.toLowerCase().includes(
            searchText.toLowerCase()
          ) ||
          item.Subcategory.toLowerCase().includes(searchText.toLowerCase())
      );
      console.log(filtered.length, "search data lnegth");
      setFinalData(filtered);
    }
  };
  useEffect(() => {
    filterData();
  }, [searchText]);
  const handleFilterChange = (categories, priceRange) => {
    setSelectedCategories(categories);
    setSelectedPriceRange(priceRange);
    setSearchText("");
    const filteredData = nameDatas.filter(
      (item) =>
        selectedCategories.includes(item.Category) &&
        item.ArticleRate >= selectedPriceRange[0] &&
        item.ArticleRate <= selectedPriceRange[1]
    );

    setFinalData(filteredData);
  };
  useEffect(() => {
    console.log(selectedCategories, "Sc");
    console.log(selectedPriceRange, "Range");
    const abc = nameDatas.filter(
      (item) =>
        (!selectedCategories.length ||
          selectedCategories.includes(item.Category)) &&
        item.ArticleRate >= selectedPriceRange[0] &&
        item.ArticleRate <= selectedPriceRange[1]
    );
    console.log(abc.length);
    setFinalData(abc);
  }, [selectedCategories, selectedPriceRange]);

  const handleCloseFilter = (isClosed) => {
    setIsFilterVisible(isClosed);
  };

  useEffect(() => {
    const minRate = nameDatas.reduce((min, item) => {
      const articleRate = parseFloat(item.ArticleRate); // Convert the article rate to a number
      return articleRate < min ? articleRate : min;
    }, Infinity);

    const maxRate = nameDatas.reduce((max, item) => {
      const articleRate = parseFloat(item.ArticleRate); // Convert the article rate to a number
      return articleRate > max ? articleRate : max;
    }, -Infinity);

    setMinArticleRate(minRate);
    console.log(minArticleRate);
    setMaxArticleRate(maxRate);
    console.log(maxArticleRate);
  }, [nameDatas]);
  const checkUserLogin = async () => {
    const token = await AsyncStorage.getItem("UserData");

    if (token) {
      console.log(token, "------------");
      setIsLoggedIn(true);
    } else {
      console.log(token, "()()()()(");
      setIsLoggedIn(false);
    }
  };

  useEffect(() => {
    checkUserLogin();
  }, []);
  return (
    <>
      {isLoading ? (
        <View style={styles.loader}>
          <ActivityIndicator size="large" color="black" />
        </View>
      ) : (
        <View
          style={{
            width: "100%",
            height: "100%",
            backgroundColor: "#FFF",
            paddingStart: 5,
            marginBottom: width >= 768 ? 10 : 0,
          }}
        >
          <View style={{ marginTop: 0 }}>
            <View>
              <Text
                style={{
                  fontSize: width >= 768 ? 25 : 22,
                  fontWeight: 700,
                  paddingLeft: 8,
                  height: 30,
                  alignItems: "center",
                  // fontFamily: "Glory-Regular",
                }}
              >
                Welcome
              </Text>
            </View>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                width: "90%",
              }}
            >
              <SearchBar
                searchPhrase={searchText}
                setSearchPhrase={setSearchText}
              />
              <TouchableOpacity
                style={{ width: "10%", alignItems: "flex-end", paddingEnd: 10 }}
                onPress={() => {
                  isLoggedIn ? openFilter() : openCreateAccountModal();
                }}
              >
                <Image
                  source={require("../../../assets/filetr_icone.png")}
                  style={{
                    width: width >= 768 ? 65 : 40, // Adjust the width for tablets
                    height: width >= 768 ? 65 : 40,
                    resizeMode: "contain",
                    borderRadius: 10,
                  }}
                />
              </TouchableOpacity>
            </View>
          </View>
          <ScrollView
            showsHorizontalScrollIndicator={false}
            style={{ overflow: "hidden" }}
          >
            <View style={{ width: "100%", flexDirection: "row", top: 10 }}>
              <Text
                style={{
                  start: 10,
                  fontWeight: 700,
                  fontSize: width >= 768 ? 25 : 18,
                }}
              >
                Men's
              </Text>
              <Text
                style={{
                  position: "absolute",
                  color: "rgba(102, 102, 102, 1)",
                  end: 10,
                  fontSize: width >= 768 ? 20 : 12,
                  fontWeight: 600,
                }}
                onPress={() => {
                  isLoggedIn ? viewAllArticles() : openCreateAccountModal();
                }}
              >
                View All
              </Text>
            </View>

            <View
              style={{
                position: "relative",
                maxWidth: "100%",
                height: "auto",
                flexDirection: "row",
                marginTop: "3%",
              }}
            >
              <ScrollView
                horizontal={true}
                showsHorizontalScrollIndicator={false}
                style={{ flex: 1, overflow: "hidden" }}
              >
                {ApplyStatushBack === true
                  ? finalData.length > 0
                    ? finalData.map((item) => (
                        <TouchableOpacity
                          onPress={() => {
                            isLoggedIn
                              ? navigation.navigate("DetailsOfArticals", {
                                  id: item.Id,
                                })
                              : openCreateAccountModal();
                          }}
                        >
                          <View
                            key={item.id}
                            style={{
                              alignItems: "center",
                              width: width >= 768 ? 300 : 155, // Adjust the width for tablets
                              height: width >= 768 ? 280 : 280,
                              marginLeft: 10,
                              marginRight: 5,
                              borderRadius: 10,
                            }}
                            // style={styles.contener2}
                          >
                            <View
                              style={{
                                width: width >= 768 ? 300 : 155, // Adjust the width for tablets
                                height: width >= 768 ? 280 : 190,
                                borderColor: "gray",
                                shadowColor: "rgba(0, 0, 0, 0.5)",
                                shadowOpacity: 0.9,
                                shadowRadius: 3,
                                borderRadius: 10,
                                elevation: 4, // For Android, use elevation
                                shadowOffset: {
                                  width: 0,
                                  height: 0,
                                },
                              }}
                              // style={styles.fastconimage1}
                            >
                              <View id={item.id} style={styles.producticones}>
                                {selectedprd.some((i) => i.Id === item.Id) ? (
                                  <TouchableOpacity
                                    onPress={() => {
                                      isLoggedIn
                                        ? rmvProductWishlist(item)
                                        : openCreateAccountModal();
                                    }}
                                  >
                                    <FontAwesome
                                      name="heart"
                                      style={[
                                        styles.icon,
                                        // isLoggedin === false ? styles.disabledIcon : null,
                                      ]}
                                    />
                                  </TouchableOpacity>
                                ) : (
                                  <TouchableOpacity
                                    onPress={() => {
                                      isLoggedIn
                                        ? addArticleWishlist(item)
                                        : openCreateAccountModal();
                                    }}
                                  >
                                    <FontAwesome
                                      name="heart-o"
                                      style={[
                                        styles.disabledIcon,
                                        // isLoggedin === false ? styles.disabledIcon : null,
                                      ]}
                                    />
                                  </TouchableOpacity>
                                )}
                              </View>
                              {item.Photos ? (
                                <Image
                                  source={{ uri: baseImageUrl + item.Photos }}
                                  style={{
                                    width: "100%",
                                    resizeMode: "contain",
                                    height: width >= 768 ? 280 : 190,
                                    borderRadius: 10,
                                  }}
                                  // style={styles.fastconimage}
                                />
                              ) : (
                                <Image
                                  source={require("../../../assets/demo.png")}
                                  style={{
                                    width: "100%",
                                    height: width >= 768 ? 280 : 190,
                                    borderRadius: 10,
                                  }}
                                  // style={styles.fastconimage}
                                />
                              )}
                            </View>

                            <Text
                              style={{
                                fontWeight: "bold",
                                marginTop: 10,
                                fontSize: width >= 768 ? 20 : 15,
                              }}
                            >
                              {item.ArticleNumber}
                            </Text>
                            <Text>{convertToTitleCase(item.Category)}</Text>
                            <Text style={{ fontWeight: "bold" }}>
                              {"₹" + item.ArticleRate + ".00"}
                            </Text>
                          </View>
                        </TouchableOpacity>
                      ))
                    : nameData.map((item) => (
                        <View
                          key={item.id}
                          style={{
                            alignItems: "center",
                            height: "auto",
                            width: width >= 768 ? 300 : 165,
                            marginLeft: 5,
                            marginRight: 5,
                            marginTop: 10,
                            marginBottom: 10,
                            borderRadius: 10,
                          }}
                          // style={styles.contener2}
                        >
                          <TouchableOpacity
                            onPress={() => {
                              isLoggedIn
                                ? handlePress(item)
                                : openCreateAccountModal();
                            }}
                          >
                            <View
                              style={{
                                width: width >= 768 ? 300 : 155, // Adjust the width for tablets
                                height: width >= 768 ? 280 : 190,
                                borderColor: "gray",
                                shadowColor: "gray",
                                shadowOpacity: 0.9,
                                shadowRadius: 10,
                                elevation: 10,
                                shadowOffset: {
                                  width: 0,
                                  height: 0,
                                },
                              }}
                              // style={styles.fastconimage1}
                            >
                              {item.Photos ? (
                                <Image
                                  source={{ uri: baseImageUrl + item.Photos }}
                                  style={{
                                    width: "100%",
                                    resizeMode: "contain",
                                    height: width >= 768 ? 280 : 190,
                                    borderRadius: 10,
                                  }}
                                  // style={styles.fastconimage}
                                />
                              ) : (
                                <Image
                                  source={require("../../../assets/demo.png")}
                                  style={{
                                    width: "100%",
                                    height: width >= 768 ? 280 : 190,
                                    borderRadius: 10,
                                  }}
                                  // style={styles.fastconimage}
                                />
                              )}
                            </View>
                          </TouchableOpacity>
