                    ))}
              </ScrollView>
            </View>
            <View></View>
            <View style={{ marginTop: width >= 768 ? 30 : 10 }}>
              <View
                style={{
                  width: "100%",
                  flexDirection: "row",
                  top: 10,
                  marginTop: 5,
                }}
              >
                <Text
                  style={{
                    start: 10,
                    fontWeight: 700,
                    fontSize: width >= 768 ? 25 : 18,
                  }}
                >
                  Kid’s
                </Text>
                <Text
                  style={{
                    position: "absolute",
                    end: 10,
                    color: "rgba(102, 102, 102, 1)",
                    fontSize: width >= 768 ? 18 : 12,
                    fontWeight: 600,
                  }}
                  onPress={() => {
                    isLoggedIn ? viewAllArticles() : openCreateAccountModal();
