                  horizontal={true}
                  showsHorizontalScrollIndicator={false}
                  style={{ flex: 1, overflow: "hidden" }}
                >
                  {ApplyStatushBack === true
                    ? nameDatas.map((item) => (
                        <View
                          key={item.id}
                          style={{
                            alignItems: "center",
                            width: width >= 768 ? 300 : 155, // Adjust the width for tablets
                            height: width >= 768 ? 280 : 280,
                            marginLeft: 10,
                            marginRight: 5,
                            marginBottom: 120,
                            borderRadius: 10,
                          }}
                        >
                          <View
                            style={{
                              width: width >= 768 ? 300 : 155, // Adjust the width for tablets
                              height: width >= 768 ? 280 : 190,
                              borderColor: "gray",
                              shadowColor: "rgba(0, 0, 0, 0.5)",
                              shadowOpacity: 0.9,
                              shadowRadius: 3,
                              borderRadius: 10,
                              elevation: 4, // For Android, use elevation
                              shadowOffset: {
                                width: 0,
                                height: 0,
                              },
                            }}
                          >
                            <View id={item.id} style={styles.producticones}>
                              {selectedprd.some((i) => i.Id === item.Id) ? (
                                <TouchableOpacity
                                  onPress={() => {
                                    isLoggedIn
                                      ? rmvProductWishlist(item)
                                      : openCreateAccountModal();
                                  }}
                                >
                                  <FontAwesome
                                    name="heart"
                                    style={[
                                      styles.icon,
                                      // isLoggedin === false ? styles.disabledIcon : null,
                                    ]}
                                  />
                                </TouchableOpacity>
                              ) : (
                                <TouchableOpacity
                                  onPress={() => {
                                    isLoggedIn
                                      ? addArticleWishlist(item)
                                      : openCreateAccountModal();
                                  }}
                                >
                                  <FontAwesome
                                    name="heart-o"
                                    style={[
                                      styles.disabledIcon,
                                      // isLoggedin === false ? styles.disabledIcon : null,
                                    ]}
                                  />
                                </TouchableOpacity>
                              )}
                            </View>
                            <Image
                              source={{ uri: baseImageUrl + item.Photos }}
                              style={{
                                flex: 1,
                                resizeMode: "contain",
                                borderRadius: 10,
                              }}
                            />
                          </View>

                          <Text
                            style={{
                              fontWeight: "bold",
                              marginTop: 10,
                              fontSize: width >= 768 ? 18 : 12,
                            }}
                          >
                            {item.ArticleNumber}
                          </Text>
                          <Text style={{ fontSize: width >= 768 ? 15 : 10 }}>
                            {convertToTitleCase(item.Category)}
                          </Text>
                          <Text
                            style={{
                              fontWeight: "bold",
                              fontSize: width >= 768 ? 18 : 12,
                            }}
                          >
                            {"₹" + item.ArticleRate + ".00"}
                          </Text>
                        </View>
                      ))
                    : applyrData.map((item) => (
                        <View
                          key={item.id}
                          style={{
                            alignItems: "center",
                            justifyContent: "center",
                            width: width >= 768 ? 300 : 155, // Adjust the width for tablets
                            height: width >= 768 ? 280 : 232,
                            marginLeft: 5,
                            marginRight: 5,
                          }}
                        >
                          <Image
                            source={{ uri: baseImageUrl + item.Photos }}
                            style={{
                              flex: 1,
                              resizeMode: width >= 768 ? "contain" : "contain",
                              borderRadius: 10,
                            }}
                          />
                          <Text style={{ fontWeight: "bold" }}>
                            {item.ArticleNumber}
                          </Text>
                          <Text>{convertToTitleCase(item.Category)}</Text>
                          <Text style={{ fontWeight: "bold" }}>
                            {"₹" + item.ArticleRate + ".00"}
                          </Text>
                        </View>
                      ))}
                </ScrollView>
              </View>
            </View>
          </ScrollView>
        </View>
      )}
      {isFilterVisible ? null : (
        <View style={{ position: "absolute", bottom: 0, left: 0, right: 0 }}>
          <ButtomNavigation
            navigation={navigation}
            isLoggedIn={isLoggedIn}
            page="home"
          />
        </View>
      )}

      {isFilterVisible && (
        <View
          style={{
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            width: "100%",
            height: "100%",
            position: "absolute",
            top: 0,
            right: 0, // Updated to 0
            left: 0,
            zIndex: 2,
          }}
        >
          <View
            style={{
              width: "92%",
              backgroundColor: "white",
              position: "absolute",
              bottom: 0,
              marginLeft: "4%",
              padding: 5,
              borderTopLeftRadius: 20,
              borderTopRightRadius: 20,
            }}
          >
            <Filter
              status={false}
              onFilterChange={handleFilterChange}
              onCloseFilter={handleCloseFilter}
              Scategories={selectedCategories}
              minArticleRate={minArticleRate}
              maxArticleRate={maxArticleRate}
            />
          </View>
        </View>
      )}
      <Modal
        visible={isCreateAccountVisible}
        animationType="slide"
        transparent={true}
        onRequestClose={closeCreateAccountModal}
      >
        <View
          style={{
            flex: 1,
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "rgba(0, 0, 0, 0.4)",
          }}
        >
          <View
            style={{
              width: "100%", // Adjust the width as needed
              backgroundColor: "#fff",
              borderRadius: 10,
              padding: 10,
              marginTop: 25,
              marginBottom: 25,
            }}
          >
            <CreateAccount onClose={closeCreateAccountModal} />
          </View>
        </View>
      </Modal>
    </>
  );
}
