import React, { useState } from "react";
import { View, Text, TouchableOpacity, TextInput ,Image} from "react-native";

export default function FilterComponent({
  categoriesData,
  catagoryHandler,
  clearFilters,
  applyFilters,
}) {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [filterText, setFilterText] = useState("");

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    catagoryHandler(category);
  };

  return (
    <View>
    <View style={{ flexDirection: "row", alignItems: "center", justifyContent: "space-between" }}>
        <Text style={{ fontSize: 18, fontWeight: "bold" }}>Categories</Text>
        <Image
          source={require("../../../assets/AppFooterIcons/1000005774.svg")}
          style={{ width: 30, height: 35 }}
        />
      </View>
     
      <TextInput
        placeholder="Filter by category..."
        value={filterText}
        onChangeText={(text) => setFilterText(text)}
        style={{
          borderBottomWidth: 1,
          borderColor: "gray",
          marginBottom: 10,
        }}
      />

      {categoriesData
        .filter((category) =>
          filterText
            ? category.Category.toLowerCase().includes(filterText.toLowerCase())
            : true
        )
        .map((category) => (
          <TouchableOpacity
            key={category.id}
            onPress={() => handleCategorySelect(category.Category)}
            style={{
              flexDirection: "row",
              alignItems: "center",
              marginVertical: 5,
            }}
          >
            <View
              style={{
                width: 20,
                height: 20,
                borderRadius: 10,
                borderWidth: 2,
                borderColor:
                  selectedCategory === category.Category ? "blue" : "gray",
                justifyContent: "center",
                alignItems: "center",
                marginRight: 10,
              }}
            >
              {selectedCategory === category.Category && (
                <View
                  style={{
                    width: 12,
                    height: 12,
                    borderRadius: 6,
                    backgroundColor: "blue",
                  }}
                />
              )}
            </View>
            <Text>{category.Category}</Text>
          </TouchableOpacity>
        ))}

      <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          marginTop: 10,
        }}
      >
        <TouchableOpacity
          onPress={() => setSelectedCategory(null)}
          style={{
            backgroundColor: "black", // Background color for Reset button
            padding: 10,
            alignItems: "center",
            borderRadius: 5,
            flex: 1,
            width: "30%", // Takes half the available space
          }}
        >
          <Text style={{ color: "white" }}>Reset</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={applyFilters}
          style={{
            backgroundColor: "black", // Background color for Apply button
            padding: 10,
            alignItems: "center",
            borderRadius: 5,
            flex: 1, // Takes half the available space
            marginLeft: 80,
            // Add margin for spacing
          }}
        >
          <Text style={{ color: "white" }}>Apply</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
