import { View, Text, Image, ScrollView, TouchableOpacity, TextInput, Pressable } from "react-native";
import { useLayoutEffect } from "react";
import MenuBackArrow from '../../components/menubackarrow/menubackarrow';
import { useEffect, useState } from "react";
import axios from 'axios'
import { cartdetails, deletecartitem } from "../../api/api";
const baseImageUrl = 'https://colorhunt.in/colorHuntApi/public/uploads/'

const AddToCart = (props) => {
    const { navigation } = props;

    useLayoutEffect(() => {
        navigation.setOptions({
            headerLeft: () => (
                <MenuBackArrow
                    onPress={() => {
                        navigation.navigate('Home');
                    }}
                />
            ),
            headerTitle: () => (
                <View style={{
                    display: "flex",
                    flexDirection: "row",
                    width: "100%"
                }}>
                    <Text style={{
                        textAlign: "center",
                        fontSize: 25, fontWeight: 700, width: "100%"
                    }}>Cart</Text>
                </View>
            ),
            headerRight: () =>
                <View style={{ marginRight: 20, width: 50, height: 40, display: "flex", justifyContent: "center", alignItems: "center" }}>
                    <TouchableOpacity  onPress={handleGoToOrderList}>
                    <Image  source={require('../../../assets/sidebaricons/icon.png')} style={{ width: 28, height: 28, borderRadius: 5, backgroundColor: "black" }} ></Image>
                    </TouchableOpacity>
                </View>,
        });
    }, []);


    // const navigate = useNavigate()
    const [promoCode, setPromoCode] = useState('')
    const [orderItems, setOrderItems] = useState([])

    const cartDetails = async () => {
        await cartdetails().then((response) => {
            console.log('Api response :', response.data)
            const parsedOrderItems = response.data.map((item) => ({
                ...item,
                Quantity: JSON.parse(item.Quantity),
            }))
            setOrderItems(parsedOrderItems)
        })
            .catch((error) => {
                console.log('Error fetching data:', error)
            })
    }
    useEffect(() => {
        cartDetails();
    }, [])

    const handlePromoCodeChange = (event) => {
        setPromoCode(event.target.value)
    }

    const handleApplyPromoCode = () => {
        // Implement logic to apply the promo code here
    }

    const handleAddMoreItems = () => {
        navigation.navigate("Home");
    }

    const handleGoBack = () => {
        //   navigate(-1)
    }

    const handleGoToOrderList = () => {
        //   navigate('/orderplaced')
    }

    const handleProceedToCheckout = () => {
        const datatopass = [orderItems]
        localStorage.setItem('Orderlist', JSON.stringify(datatopass))
        //   navigate(`/orders`) // Update the route path as per your routing setup
    }

    const handleDeleteOrder = async (article_id) => {
        // console.log(article_id);
        const data = {
            party_id: 197,
            article_id: article_id,
        }
        try {
            await deletecartitem(data);
            const updatedcartitems = orderItems.filter((item) => item.article_id !== article_id)
            setOrderItems(updatedcartitems)
        } catch (error) {
            console.log('Erro deleting article:', error)
        }
    }
    const handleEditOrder = (article_id) => {
        const ArticalId = article_id
        // const PartyId = 197
        //   navigate(`/editarticledetails/${ArticalId}`)
        //   navigation.navigate('',{}) disable for this time....
        // navigate('/editarticledetails')
    }
    const totalItems = orderItems.length
    const totalPrice = orderItems.reduce((total, item) => total + item.rate, 0)
    const cartIsEmpty = orderItems.length === 0

    const getSingaleartical = (item) => {
        const ArticalId = item.article_id
        console.log(ArticalId)
        //   navigate(`/Articles-details/${ArticalId}`) // Pass the ArticalId as a URL parameter to /Articles-details screen
        navigation.navigate("DetailsOfArticals", { id: ArticalId });
    }
    return (
        <View >
            <View style={{ height: "100%", width: "100%", backgroundColor: "white", borderTopColor: "black", borderWidth: 1, borderStyle: "solid" }}>
                <View style={{ display: "flex", flexDirection: "column", height: "60%", width: "100%", backgroundColor: "#FFF" }}>
                    <ScrollView>
                        <View style={{ paddingBottom: 20 }}>
                            {orderItems.map((item) =>
                                <View key={item.id} style={{
                                    display: "flex",
                                    flexDirection: "row",
                                    width: "95%",
                                    backgroundColor: "#FFF",
                                    elevation: 5,
                                    marginHorizontal: 9.5,
                                    marginTop: 15,
                                    borderRadius: 10,
                                    height: 120
                                }}>
                                    <View style={{
                                        width: "25%",
                                        display: "flex",
                                        justifyContent: "center",
                                        alignItems: "center",
                                        marginLeft: 15,
                                        marginVertical: 10,
                                        borderRadius: 10,
                                    }}>
                                        <Image style={{ height: "100%", width: "100%", borderRadius: 10 }} source={{ uri: baseImageUrl + item.Photos.split(',')[0] }}></Image>
                                    </View>
                                    <View style={{
                                        width: "45%",
                                        marginHorizontal: 4,
                                        marginVertical: 10,
                                        borderRadius: 10
                                    }}>
                                        <View>
                                            <Text style={{
                                                fontSize: 18,
                                                fontWeight: 700, color: "#000"
                                            }}>{item.ArticleNumber}</Text>
                                            <Text style={{
                                                fontSize: 14,
                                                fontWeight: 400, color: "#000"
                                            }}>{item.StyleDescription}</Text>
                                        </View>
                                        <View style={{ marginTop: "10%" }}>
                                            <Text style={{
                                                fontSize: 14,
                                                fontWeight: 400, color: "#000"
                                            }}>Rate:</Text>
                                            <Text style={{
                                                fontSize: 17,
                                                fontWeight: 700, color: "#000"
                                            }}>₹{item.rate}</Text>
                                        </View>
                                    </View>
                                    <View style={{
                                        width: "18%",
                                        display: "flex",
                                        flexDirection: "row",
                                        justifyContent: "flex-end",
                                        gap: 8,
                                        marginLeft: 15,
                                        marginVertical: 10,
                                        borderRadius: 10,

                                    }}>
                                        <TouchableOpacity
                                            onPress={() => handleEditOrder(item.article_id)}
                                        >
                                            <Image alt="edite"
                                                style={{ width: 20, height: 20, backgroundColor: "green" }} source={require("../../../assets/sidebaricons/edit (5) 1.png")}></Image>
                                        </TouchableOpacity>
                                        <TouchableOpacity
                                            onPress={() => handleDeleteOrder(item.article_id)}
                                        >
                                            <Image alt="Delete"
                                                style={{ width: 20, height: 20, backgroundColor: "black" }} source={require("../../../assets/sidebaricons/edit (5) 1.png")}></Image>
                                        </TouchableOpacity>
                                    </View>
                                </View>
                            )}
                        </View>

                    </ScrollView>

                </View>
                <ScrollView style={{ height: "30%", width: "100%", backgroundColor: "#FFF" }}>
                    <View style={{ padding: 10 }}>
                        <TextInput
                            value={promoCode}
                            onChange={handlePromoCodeChange}
                            style={{
                                width: "100%",
                                borderWidth: 1, paddingVertical: 18,
                                paddingLeft: 15, borderRadius: 10,
                                fontSize: 18, backgroundColor: "#EEE"
                            }} keyboardType="default" placeholder="Promo Code">
                        </TextInput>
                        <Pressable onPress={handleApplyPromoCode} style={{
                            width: "100%",
                            position: "absolute",
                            top: "33.5%",
                            left: "70%",
                            right: 0
                        }}>
                            <Text style={{
                                color: "white",
                                backgroundColor: "#212121",
                                borderRadius: 7.6, width: 100, paddingHorizontal: 18,
                                paddingVertical: 9,
                                fontSize: 18, fontWeight: 600,
                                textAlign: "center"
                            }}>Apply</Text>
                        </Pressable>
                    </View>
                    <View style={{
                        display: "flex",
                        justifyContent: "flex-end",
                        alignItems: "flex-end",
                        width: "97.5%",
                        marginTop: "5%"
                    }}>
                        <Pressable onPress={handleAddMoreItems}>
                            <Text style={{
                                color: "white",
                                backgroundColor: "#212121",
                                borderRadius: 7.6, width: 150, paddingHorizontal: 18,
                                paddingVertical: 9,
                                fontSize: 18, fontWeight: 600,
                                textAlign: "center"
                            }}>
                                Add more
                            </Text>
                        </Pressable>
                    </View>
                    <View style={{
                        display: "flex",
                        flexDirection: "row",
                        marginTop:"8%",
                        // width: "100%",
                    }}>
                        <View style={{width:"50%",paddingLeft:"2.8%"}}>
                            <Text>Total ({totalItems} item) :</Text>
                        </View>
                        <View style={{
                            display: "flex",
                            flexDirection: "row", gap: 5,
                            width:"50%",
                            paddingLeft:"20%"
                        }}>
                            <Text >Total price</Text>
                            <Text >₹{totalPrice}</Text>
                        </View>
                    </View>
                    <View  style={{ padding: 10 }}>
                        <Pressable style={{width:"100%"}} onPress={handleProceedToCheckout}>
                            <Text style={{
                                color: "white",
                                backgroundColor: "#212121",
                                borderRadius: 7.6, paddingHorizontal: 20,
                                paddingVertical: 15,
                                fontSize: 18, fontWeight: 600,
                                textAlign: "left"
                            }}>
                            Proceed to Checkout
                            </Text>
                            <Image style={{  width: 30,height:30,
                            position: "absolute",
                            top: "20%",
                            left: "85%",
                            right: 0}} source={require("../../../assets/arrow.png")}></Image>
                        </Pressable>
                    </View>
                </ScrollView>
            </View>
        </View>
    )
}

export default AddToCart;
