import React, { useEffect, useLayoutEffect, useState } from "react";
import {
  Text,
  View,
  Image,
  ScrollView,
  Pressable,
  TouchableOpacity,
  Keyboard,
  TextInput,
  Button,
} from "react-native";
import MenuImage from "../../components/MenuImage/MenuImage";
import styles from "./styles";
import { getProductName, getcateGorywithphotos } from "../../api/api";
import ButtomNavigation from "../../components/AppFooter/ButtomNavigation";
import SearchBar from "../../components/SearchBar/searchbar";
import Filter from "../../components/Fliter/Filter";
import { ActivityIndicator } from "react-native";

export default function HomeScreen(props) {
  const { navigation } = props;
  const [categoryName, setCategoryName] = useState();
  const [ApplyStatushBack, setApplyStatushBack] = useState(true);
  const [nameData, setNameData] = useState([]);
  const [nameDatas, setNameDatas] = useState([]);
  const [applyrData, setApplyData] = useState([]);
  const [isFilterVisible, setIsFilterVisible] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const [rateRange, setrateRange] = useState([0, 1000]);
  // open filter


  //Search Functionaity - Harshil
  const [searchText, setSearchText] = useState(""); // To store the search text
  const [filteredData, setFilteredData] = useState([...nameDatas]); // Initialize with your data
  const [filterDataSearch, setFilterDataSearch] = useState([])



  const openFilter = () => {
    setIsFilterVisible((prev) => !prev); // Toggle the Filter component visibility
  };

  const applyFilters = (selectedCategories) => {
    // Filter your data based on selectedCategories here
    console.log(rateRange, "{}{}{}{}{}{}");
    let filteredData = [];
    // if (rateRange[0]!==0 || rateRange[1]!==1000) {
    //   const min = parseFloat(rateRange[0])
    //   const max = parseFloat(rateRange[1])
    //   if (min >= 0 && max <= 1000) {
    //     filteredData = applyrData.filter((product) => {
    //       return product.rate >= min && product.rate <= max
    //     })
    //     console.log(sdPrds)
    //   }
    // }
    if (selectedCategories) {
      filteredData = applyrData.filter((item) =>
        selectedCategories.includes(item.Category)
      );
    }

    // Update the state to display the filtered data
    setNameData(filteredData);
    setIsFilterVisible(false); //close the filter
  }

  // uploard url image
  const baseImageUrl = "https://colorhunt.in/colorHuntApi/public/uploads/";
  //getCategoriesname
  const getCategoriesname = async () => {
    try {
      const result1 = await getcateGorywithphotos();
      if (result1.status === 200) {
        setCategoryName(result1.data);
        setNameData(result1.data);
        setApplyData(result1.data);
      }

      const result2 = await getProductName();
      if (result2.status === 200) {
        setCategoryName(result2.data);
        setNameDatas(result2.data);
        setApplyData(result2.data);
        setFilterDataSearch(result2.data)
      }

      setIsLoading(false);
    } catch (error) {
      // Handle any errors that might occur during the API requests.
      console.error(error);
      setIsLoading(false); // Make sure to set isLoading to false in case of an error.
    }
  };


  useEffect(() => {
    getCategoriesname();
  }, []);

  const viewAllArticles = () => {
    navigation.navigate("AllArticle", { filteredData });
  };

  useLayoutEffect(() => {
    navigation.setOptions({
      headerLeft: () => (
        <View style={{ marginLeft: 5, width: 50, height: 100, display: "flex", justifyContent: "center", alignItems: "center" }}>
          <TouchableOpacity onPress={() => {
            navigation.openDrawer();
          }}>
            <Image source={require('../../../assets/sidbarOpenIcone.png')} style={{ width: 38, height: 38, borderRadius: 5 }} ></Image>
          </TouchableOpacity>
        </View>

      ),
      headerTitle: () => (
        <View style={styles.searchContainer}>

          <Pressable >
          </Pressable>
        </View>
      ),
      headerRight: () => <View />,
    });
  }, []);

  const onPressRecipe = (item) => {
    const id = 883;
    navigation.navigate("DetailsOfArticals", { id: id });
  };

  const handlePress = (item) => {
    // ategoryNames(item);
    console.log(item);
    navigation.navigate("CategorisWiseArticle", { item: item });
  };

  const filterData = () => {
    if (searchText === '') {
      setFilteredData(nameDatas)
    } else {
      console.log(filterDataSearch.length)
      const filtered = filterDataSearch.filter((item) =>
        item.ArticleNumber.toString().includes(searchText.toString()) ||
        item.Category.toLowerCase().includes(searchText.toLowerCase()) ||
        item.ArticleRate.toString().includes(searchText.toString()) ||
        item.StyleDescription.toLowerCase().includes(searchText.toLowerCase()) ||
        item.Subcategory.toLowerCase().includes(searchText.toLowerCase()),
      )
      setFilteredData(filtered)
      console.log(filteredData.length)
    }
  }
  useEffect(() => {
    filterData();
  }, [searchText])
  return (
    <>
      {isLoading ? (
        <View style={styles.loader}>
          <ActivityIndicator
            size="large"
            color="black"
          />
        </View>
      ) : (
        <View style={{ width: "100%", height: "100%", backgroundColor: "#FFF" }}>
          <View style={{ marginTop: 10 }}>
            <View>
              <Text
                style={{
                  fontSize: 25,
                  fontWeight: 700,
                  paddingLeft: 20,
                  height: 40,
                  alignItems: "center",
                }}
              >
                Welcome
              </Text>
            </View>
            <View
              style={{ flexDirection: "row", alignItems: "center", width: "87%" }}
            >
              <SearchBar
                searchPhrase={searchText}
                setSearchPhrase={setSearchText} />
              <TouchableOpacity onPress={openFilter}>
                <Image
                  source={require("../../../assets/filetr_icone.png")}
                  style={{ width: 50, height: 50, borderRadius: 10 }}
                />
              </TouchableOpacity>
            </View>
          </View>
          <ScrollView
            showsHorizontalScrollIndicator={false}
            style={{ overflow: "hidden" }}
          >
            <View style={{ width: "100%", flexDirection: "row", top: 10 }}>
              <Text style={{ start: 10, fontWeight: 700, fontSize: 18 }}>Men's </Text>
              <Text
                style={{ position: "absolute", end: 10, fontSize: 12, fontWeight: 600 }}
                onPress={viewAllArticles}
              >
                View All
              </Text>
            </View>

            <View
              style={{
                position: "relative",
                maxWidth: "100%",
                height: "auto",
                flexDirection: "row",
                top: 20,
              }}
            >
              <ScrollView
                horizontal={true}
                showsHorizontalScrollIndicator={false}
                style={{ flex: 1, overflow: "hidden" }}
              >
                {ApplyStatushBack === true ? (searchText.length > 0 ? (filteredData.map((item) => (
                  <TouchableOpacity onPress={() => navigation.navigate("DetailsOfArticals", { id: item.Id })}>
                    {/* <View
                key={item.id}
                style={{
                  alignItems: "center",
                  marginLeft: 5,
                  marginRight: 5,
                  borderRadius: 10,
                  shadowOpacity: 0.8,
                  shadowRadius: 4,
                  elevation: 5,
                  shadowOffset: {
                    width: 0,
                    height: 2,
                  },
                  // Add elevation for Android
                  elevation: 4,
                }}
              >
                <Image
                  source={{ uri: baseImageUrl + item.Photos }}
                  style={{ width: 155, height: 190, elevation: 5, borderRadius: 10, paddingRight: 10, paddingLeft: 10 }}
                />
                <Text style={{ fontWeight: "bold", marginTop: 10 }}>
                  {item.ArticleNumber}
                </Text>
                <Text>{item.Category}</Text>
                <Text style={{ fontWeight: "bold" }}>
                  {"₹" + item.ArticleRate}
                </Text>
              </View> */}
                  </TouchableOpacity>
                ))) : (nameData.map((item) => (
                  <View
                    key={item.id}
                    style={{
                      alignItems: "center",
                      height: 'auto',
                      width: 180,
                      marginLeft: 5,
                      marginRight: 5,
                      marginTop: 10,
                      marginBottom: 10,
                      borderRadius: 10,

                    }}
                  >
                    <TouchableOpacity
                      onPress={() => {
                        handlePress(item);
                      }}
                    >
                      <View style={{
                        borderColor: "gray",
                        width: 155,
                        height: 170,
                        shadowColor: "#000000",
                        shadowOpacity: 0.4,
                        shadowRadius: 4,
                        elevation: 10, // This is for Android, use shadow properties for iOS
                        shadowOffset: {
                          width: 0,
                          height: 0,
                        },
                      }}>
                        <Image
                          source={require("../../../assets/demo.png")}
                          style={{
                            width: 155,
                            height: 170,
                            borderRadius: 10,

                          }}
                        />
                      </View>


                    </TouchableOpacity>
                    <Text style={{ marginTop: 10, fontWeight: "bold", marginBottom: 10 }}>
                      {item.Category}
                    </Text>
                  </View>
                ))))
                  : applyrData.map((item) => (
                    <View
                      key={item.id}
                      style={{
                        alignItems: "center",
                        justifyContent: "center",
                        width: 200,
                        marginLeft: 5,
                        marginRight: 5,
                      }}
                    >
                      <TouchableOpacity
                        onPress={() => {
                          handlePress(item);
                        }}
                      >
                        <Image
                          source={require("../../../assets/demo.png")}
                          style={{ width: 200, height: 300, borderRadius: 10 }}
                        />
                      </TouchableOpacity>
                      <Text style={{ marginTop: 10, fontWeight: "bold" }}>
                        {item.Category}
                      </Text>
                    </View>
                  ))}
              </ScrollView>
            </View>
            <View>

            </View>
            <View style={{ marginTop: 10 }}>
              <View
                style={{
                  width: "100%",
                  flexDirection: "row",
                  top: 10,
                  marginTop: 20,
                }}
              >
                <Text style={{ start: 10, fontWeight: 700, fontSize: 18 }}>Kid’s </Text>
                <Text
                  style={{ position: "absolute", end: 10, fontSize: 12, fontWeight: 600 }}
                  onPress={viewAllArticles}
                >
                  View All
                </Text>
              </View>

              <View
                style={{
                  position: "relative",
                  maxWidth: "100%",
                  height: "auto",
                  flexDirection: "row",
                  top: 20,
                }}
              >
                <ScrollView
                  horizontal={true}
                  showsHorizontalScrollIndicator={false}
                  style={{ flex: 1, overflow: "hidden" }}
                >
                  {ApplyStatushBack === true
                    ? nameDatas.map((item) => (

                      <View
                        key={item.id}
                        style={{
                          alignItems: "center",
                          height: 280,
                          width: 190,
                          marginLeft: 5,
                          marginRight: 5,
                          marginBottom: 120,
                          borderRadius: 10,

                        }}
                      >
                        <View style={{
                          width: 155,
                          height: 190,
                          borderColor: "gray",
                          shadowColor: "#000000",
                          shadowOpacity: 0.4,
                          shadowRadius: 4,
                          elevation: 10, // This is for Android, use shadow properties for iOS
                          shadowOffset: {
                            width: 0,
                            height: 0,
                          },
                        

                        }}>
                          <Image
                            source={{ uri: baseImageUrl + item.Photos }}
                            style={{ width: 155, height: 190, elevation: 5, borderRadius: 10, paddingRight: 10, paddingLeft: 10 }}
                          />
                        </View>

                        <Text style={{ fontWeight: "bold", marginTop: 10 }}>
                          {item.ArticleNumber}
                        </Text>
                        <Text>{item.Category}</Text>
                        <Text style={{ fontWeight: "bold" }}>
                          {"₹" + item.ArticleRate}
                        </Text>
                      </View>
                    ))
                    : applyrData.map((item) => (
                      <View
                        key={item.id}
                        style={{
                          alignItems: "center",
                          justifyContent: "center",
                          width: 155,
                          height: 232,
                          marginLeft: 5,
                          marginRight: 5,
                        }}
                      >
                        <Image
                          source={{ uri: baseImageUrl + item.Photos }}
                          style={{ width: 200, height: 200, borderRadius: 10 }}
                        />
                        <Text style={{ fontWeight: "bold" }}>
                          {item.ArticleNumber}
                        </Text>
                        <Text>{item.Category}</Text>
                        <Text style={{ fontWeight: "bold" }}>
                          {"₹" + item.ArticleRate}
                        </Text>
                      </View>
                    ))}
                </ScrollView>
              </View>
            </View>
          </ScrollView>
          {isFilterVisible ? null : (
            <View style={{ position: "absolute", bottom: 0, left: 0, right: 0 }}>
              <ButtomNavigation navigation={navigation} />
            </View>
          )}

          {isFilterVisible && (
            <View
              style={{
                flex: 1,
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                width: "100%",
                height: "100%",
                position: "absolute",
                top: 0,
                left: 0,
                justifyContent: "flex-end",
              }}
            >
              <View
                style={{
                  width: "90%",
                  backgroundColor: "white",
                  position: "absolute",
                  bottom: 0,
                  // left: 0,
                  // right: 0, // To make it span the full width
                  marginLeft: "5%", // Margin on the left side
                  marginRight: "5%", // Margin on the right side
                  padding: 5,
                  borderTopLeftRadius: 20,
                  borderTopRightRadius: 20,
                }}
              >
                <Filter
                  categoriesData={nameData}
                  selectedCategories={selectedCategories}
                  setSelectedCategories={setSelectedCategories}
                  clearFilters={() => setIsFilterVisible(false)}
                  applyFilters={() => applyFilters(selectedCategories)}
                  setrateRange={setrateRange}
                />
              </View>
            </View>
          )}
        </View>
      )}
    </>
  );
}
