import React, { useLayoutEffect, useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
  ImageBackground,
} from "react-native";
// import { CountdownCircleTimer } from "react-native-countdown-circle-timer"; // You might need to install this package
import loginBackgroundImg from "../../../assets/Login/image.svg";
import mainlogo from "../../../assets/Login/image.svg";

const Login = (props) => {
  const { navigation } = props;

  useLayoutEffect(() => {
    // Your code inside useLayoutEffect
  }, []);
  return (
    <ImageBackground
      source={require("../../../assets/Login/mainlogo.png")} // Set the background image source
      style={{ flex: 1, justifyContent: "center", alignItems: "center" }}
    >
      <View style={{ width: "100%", alignItems: "center" }}>
        <Text style={{ fontSize: 20, marginBottom: 10 }}>Welcome!</Text>
        <Text>Please Login To Continue</Text>
        <TextInput
          style={{
            width: "80%",
            height: 40,
            borderColor: "gray",
            borderWidth: 1,
            marginTop: 20,
            backgroundColor: "white",
          }}
          placeholder="Phone Number"
          keyboardType="numeric"
          maxLength={10}
        />
        <TouchableOpacity
          style={{
            backgroundColor: "black",
            padding: 10,
            borderRadius: 14,
            marginTop: 20,
          }}
          // onPress={handleSubmit}
        >
          <Text style={{ color: "white" }}>Next</Text>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  );
};

export default Login;
